mapper.update:

update tbl_notice set
	viewcnt = viewcnt + 1
where num = #{num}

update tbl_notice_c set
	deleted = 'yes'
where c_num = #{c_num}

update tbl_notice_c set
	deleted = 'all'
where c_num = #{c_num}


update tbl_notice_c set
	deleted = 'cancell'
where c_num = #{c_num}

update tbl_notice_c set
	content = #{content}
where c_num = #{c_num}
